import React, {useContext, useState, useEffect} from 'react';
import bcrypt from 'bcryptjs';
import {auth} from '../firebase';

const AuthContext = React.createContext();

export function useAuth() {
    return useContext(AuthContext);
}

export function AuthProvider({children}) {

    const [currentUser, setCurrentUser] = useState(JSON.parse(sessionStorage.getItem('currentUser')) || null);
    const [userData, setUserData] = useState(JSON.parse(sessionStorage.getItem('userData')) || null);
    const [loading, setLoading] = useState(true);

    async function registerUser(name, email, password, type, worldId) {
        auth.createUserWithEmailAndPassword(email, password);
        const destination = type === 'applicant' ? `/worlds/${worldId}/applicants` : `/worlds/${worldId}/businesses`;
        const data = await fetch(destination, {
            method: 'POST',
            body: JSON.stringify({
                name,
                email,
                worldId
            })
        }).then(response => {
            if(response.ok){
                return response.json();
            }
        });
        setUserData(data);
        return data;
    }

    async function loginUser(email, password){
        auth.signInWithEmailAndPassword(email, password);
        const data = await fetch(`/users?email=${email}`).then(response => {
            if(response.ok){
                return response.json();
            }
        });
        setUserData(data);
        return data;
    }

    async function updateAccount(name, email, password, worldId){
        currentUser.updateEmail(email);
        password && currentUser.updatePassword(password);
        const data = await fetch(userData.links.self, {
            method: 'PUT',
            body: JSON.stringify({
                action: 'account',
                name,
                email,
                worldId
            })
        });
        setUserData(data);
    }

    async function updateFeatures(cap, gpa, majors, standings, skills, interests, courses){
        const data = await fetch(userData.links.self, {
            method: 'PUT',
            body: JSON.stringify({
                action: 'features',
                cap: parseInt(cap),
                gpa: parseFloat(gpa),
                majors,
                standings,
                skills,
                interests,
                courses
            })
        }).then(response => {
            if(response.ok){
                return response.json();
            }
        });
        setUserData(data);
    }

    async function logoutUser(){
        const fire = auth.signOut();
        setUserData(null);
        return fire;
    }

    async function deleteUser(){
        const fire = currentUser.delete();
        const data = await fetch(userData.links.self, {
            method: 'DELETE'
        }).then(response => {
            if(response.ok){
                return response.json();
            }
        });
        setUserData(null);
        return fire;
    }

    async function joinCluster(){
        const data = await fetch(userData.links.self, {
            method: 'POST',
            body: JSON.stringify({action: 'join'})
        }).then(response => {
            if(response.ok){
                return response.json();
            }
        });
        setUserData(data);
    }

    async function leaveCluster(){
        const data = await fetch(userData.links.self, {
            method: 'POST',
            body: JSON.stringify({action: 'leave'})
        }).then(response => {
            if(response.ok){
                return response.json();
            }
        });
        setUserData(data);
    }

    async function peelFromCluster(){
        const data = await fetch(userData.links.self, {
            method: 'POST',
            body: JSON.stringify({action: 'peel'})
        }).then(response => {
            if(response.ok){
                return response.json();
            }
        });
        setUserData(data);
    }

    useEffect(() => {
        const unsubscribe = auth.onAuthStateChanged(user => {
            setCurrentUser(user);
            !user ? sessionStorage.clear() :
                sessionStorage.setItem('currentUser', JSON.stringify(user));
        })
        setLoading(false)
        return unsubscribe;
    }, []);

    useEffect(() => {
        !currentUser ? sessionStorage.clear() :
            sessionStorage.setItem('userData', JSON.stringify(userData));
        setLoading(false)
    }, [userData]);

    const value = {
        currentUser,
        userData,
        registerUser,
        loginUser,
        logoutUser,
        deleteUser,
        updateAccount,
        updateFeatures,
        joinCluster,
        peelFromCluster,
        leaveCluster,
    };

    return (
        <AuthContext.Provider value={value}>
            {!loading && children}
        </AuthContext.Provider>
    );
}
