import React, {useState, useEffect} from 'react';
import {useAuth} from '../../contexts/AuthContext';
import bcrypt from 'bcryptjs'
import {Link, useHistory} from 'react-router-dom';
import { 
    Form,
    Button,
    Col
} from 'react-bootstrap';
import "./Form.css"


export function LoginForm() {

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const {loginUser} = useAuth();
    const history = useHistory();

     async function handleLoginUser() {
        try {
            setError('Logged In');
            setLoading(true);
            if(!email || !password){
                throw 'Fields are required';
            }
            const data = await loginUser(email, password);
            const destination = data.clusterId ? '/active-dashboard' : '/dormant-dashboard';
            history.push(destination);
        } catch(err) {
            setError(err);
        }
        console.log(error);
        setLoading(false);
    };


    return (

        <div className="form-container">

            <h1 className="form-header">Login</h1>

            <Form>

                <Col xs="auto">
                    <Form.Group controlId="email">
                        <Form.Label>Email Address</Form.Label>
                        <Form.Control size="lg" type="email" placeholder="Enter Email" />
                    </Form.Group>

                    <Form.Group controlId="pasword">
                        <Form.Label>Password</Form.Label>
                        <Form.Control size="lg" type="password" placeholder="Enter Password" />
                    </Form.Group>

                    <Button variant="primary" type="submit" size="lg">
                        Submit
                    </Button>

                </Col>

            </Form>

        </div>



        // <div>
        //     <input value={email} onChange={e => setEmail(e.target.value)}/>
        //     <br/>
        //     <input type='password' value={password} onChange={e => setPassword(e.target.value)}/>
        //     <button onClick={handleLoginUser}>Submit</button>
        //     <div>
        //         Need an Account? <Link to='/register'>Register</Link>
        //     </div>
        // </div>
    )
}
