import React, {useState} from 'react';
import {useHistory} from 'react-router-dom';
import TagsInput from 'react-tagsinput'
import {useAuth} from '../../contexts/AuthContext';

export function WorldForm(){

    const [id, setId] = useState('');
    const [courses, setCourses] = useState([]);
    const [majors, setMajors] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const history = useHistory();

    async function handleRegisterWorld(){
        try {
            setError('World Created');
            setLoading(true);
            if(!id || !majors.length || !courses.length){
                throw 'Fields not filled out';
            }
            const temp = await fetch(`/worlds?id=${id}`).then(response => {
                if(response.ok){
                    return response.json();
                }
            });
            if(temp.length){
                throw 'This world has already been created';
            }
            const data = await fetch('/worlds', {
                method: 'POST',
                body: JSON.stringify({
                    id,
                    majors,
                    courses
                })
            }).then(response => {
                if(response.ok){
                    return response.json()
                }
            });
            history.push('/home');
        } catch(err) {
            setError(err);
        }
        console.log(error);
        setLoading(false);
    }

    return (
        <div>
            Name: <input value={id} onChange={e => setId(e.target.value)}/>
            <br/>
            Majors: <TagsInput value={majors} onChange={tags => setMajors(tags)}/>
            <br/>
            Courses: <TagsInput value={courses} onChange={tags => setCourses(tags)}/>
            <br/>
            <button disabled={loading} onClick={handleRegisterWorld}>Submit</button>
        </div>
    )

}
