import React, {useState} from 'react';
import {useHistory} from 'react-router-dom';
import TagsInput from 'react-tagsinput'

export function WorldForm(){

    const [name, setName] = useState('');
    const [courses, setCourses] = useState([]);
    const [majors, setMajors] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const history = useHistory();

    async function handleRegisterWorld(){
        try {
            setError('Registered');
            setLoading(true);
            if(!name || !majors.lengths || !courses.lengths){
                console.log(majors)
                throw 'Fields not filled out';
            }
            await fetch('/worlds', {
                method: 'POST',
                body: JSON.stringify({
                    name,
                    majors,
                    courses
                })
            }).then(response => {
                if(response.ok){
                    return response.json();
                }
            });
        } catch(err) {
            setError(err);
        }
        console.log(error);
        setLoading(false);
    }

    return (
        <div>
            Name: <input value={name} onChange={e => setName(e.target.value)}/>
            <br/>
            Majors: <TagsInput value={majors} onChange={tags => setMajors(tags)}/>
            <br/>
            Courses: <TagsInput value={courses} onChange={tags => setCourses(tags)}/>
            <br/>
            <button disabled={loading} onClick={handleRegisterWorld}>Submit</button>
        </div>
    )

}
