import React, {useState, useEffect} from 'react';
import {useAuth} from '../../contexts/AuthContext';
import {useHistory} from 'react-router-dom';

export function ApplicantDashboard(){

    const {currentUser} = useAuth();
    const [pool, setPool] = useState([]);
    const [applied, setApplied] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState();
    const history = useHistory();


    // handle peel
    useEffect(async () => {
        const data = await fetch(currentUser.links.pool).then(response => {
            if(response.ok){
                return response.json();
            }
        });
        setPool(data);
    }, [currentUser]);



    async function handleApply(businessId){
        try {
            setError('Successfully Applied');
            setLoading(true);
            const data = await fetch(currentUser.links.applied, {
                method: 'PUT',
                body: JSON.stringify({
                    action: 'apply',
                    businessId
                })
            }).then(response => {
                if(response.ok){
                    return response.json();
                }
            });
            setApplied(data);
        } catch (err) {
            setError('Failed to apply');
            setLoading(false);
        }
    }

    function renderPool(){
        return pool.map(user => {
            return (
                <div>
                    <h2>{user.name}</h2>
                    <li>{user.features.skills}</li>
                    <button onClick={() => handleApply(user.id)}>Apply</button>
                </div>
            )
        })
    }

    return (
        <div>
            <h1>Active Dashboard</h1>
            {renderPool()}
        </div>
    );
}
