import React, {useState, useEffect} from 'react';
import bcrypt from 'bcryptjs';
import TagsInput from 'react-tagsinput';
import {useAuth} from '../../contexts/AuthContext';
import {socket} from '../../App';

export function RegisterApplicantForm() {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [major, setMajor] = useState([]);
    const [standing, setStanding] = useState([]);
    const [gpa, setGpa] = useState(0);
    const [skills, setSkills] = useState([]);
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const {signUp, currentUser} = useAuth();

    const handleClick = async () => {
        const temp = await fetch(`/applicants?email=${email}`).then(response => {
            if(response.ok){
                return response.json();
            }
        });

        if(temp.length > 0){
            return setError('This Email has Already Been Taken');
        } else {
            try{
                setError('');
                setLoading(true);
                await signUp(email, password);
            } catch {
                setError('Failed to Create an Account');
            }
            fetch('/applicants', {
                method: 'POST',
                body: JSON.stringify({
                    name,
                    email,
                    password: bcrypt.hashSync(password, 10),
                    features: {
                        major,
                        standing,
                        gpa: parseFloat(gpa),
                        skills
                    }
                })
            }).then(response => {
                if(response.ok){
                    return response.json();
                }
                setLoading(false);
            }).then(({id, name, email, password, features, links}) => {
                console.log(id);
                socket.emit('user', id);
            });
        }
        setName('');
        setEmail('');
        setPassword('');
        setConfirmPassword('');
        setMajor([]);
        setStanding([]);
        setGpa(0);
        setSkills([]);
    }

    return (
        <div>
            {currentUser.email}
            <br/>
            Name: <input value={name} onChange={e => setName(e.target.value)}/>
            <br/>
            Email: <input value={email} onChange={e => setEmail(e.target.value)}/>
            <br/>
            Password: <input type='password' value={password} onChange={e => setPassword(e.target.value)}/>
            <br/>
            Confirm Password: <input type='password' value={confirmPassword} onChange={e => setConfirmPassword(e.target.value)}/>
            <br/>
            Major: <TagsInput value={major} onChange={tags => setMajor(tags)}/>
            <br/>
            Standing: <TagsInput value={standing} onChange={tags => setStanding(tags)}/>
            <br/>
            GPA: <input value={gpa} onChange={e => setGpa(e.target.value)}/>
            <br/>
            Skills: <TagsInput value={skills} onChange={tags => setSkills(tags)}/>
            <button disabled={loading} onClick={() => handleClick()}>Submit</button>
        </div>
    )
}
