import React, {useState, useEffect} from 'react';
import {useAuth} from '../../contexts/AuthContext';
import {useHistory} from 'react-router-dom';
import io from 'socket.io-client';


export function BusinessDashboard(){

    const {currentUser} = useAuth();
    const [pool, setPool] = useState([]);
    const [reached, setReached] = useState([]);
    const [received, setReceived] = useState([]);
    const [interested, setInterested] = useState([]);
    const [offered, setOffered] = useState([]);
    const [declined, setDeclined] = useState([]);
    const [rejected, setRejected] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState();
    const history = useHistory();

    useEffect(async () => {
        const data = await fetch(currentUser.links.all).then(response => {
            if(response.ok){
                return response.json();
            }
        });
        setPool(data.pool);
        setReached(data.reached);
        setReceived(data.received);
        setInterested(data.interested);
        setOffered(data.offered);
        setDeclined(data.declined);
        setRejected(data.rejected);
    }, [currentUser, reached, received, interested, offered, declined]);


    async function handleReachApplicant(applicantId){
        try {
            setError('Successfully Reached');
            setLoading(true);
            const data = await fetch(currentUser.links.reached, {
                method: 'PUT',
                body: JSON.stringify({
                    action: 'reach',
                    applicantId
                })
            }).then(response => {
                if(response.ok){
                    return response.json();
                }
            });
            setReached(data);
        } catch (err) {
            setError('Failed to reach out');
        }
    }


    async function handleCancelReach(applicantId){
        try {
            setError('Successfully Canceled');
            setLoading(true);
            const data = await fetch(currentUser.links.reached, {
                method: 'PUT',
                body: JSON.stringify({
                    action: 'cancel',
                    applicantId
                })
            }).then(response => {
                if(response.ok){
                    return response.json();
                }
            });
            setReached(data);
        } catch (err) {
            setError('Failed to cancel');
        }
    }


    function renderPool(){
        return pool.map(applicant => {
            return (
                <div>
                    <h3>{applicant.name}</h3>
                    <li>{applicant.features.skills}</li>
                    <button onClick={() => handleReach(applicant.id)}>Apply</button>
                </div>
            )
        })
    }


    function renderReached(){
        return reached.map(applicant => {
            return (
                <div>
                    <h3>{applicant.name}</h3>
                    <li>{applicant.features.skills}</li>
                    <button onClick={() => handleCancel(applicant.id)}>Cancel</button>
                </div>
            )
        })
    }

    return (
        <div>
            <h2>Pool</h2>
            {renderPool()}
            <br/>
            <h2>Reached</h2>
            {renderReached()}
        </div>
    );
}
